import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {deleteCarThunk, setCarFromDB} from "../../Redux/reducers/folderPageReducer";
import {AppStateType} from "../../Redux/Redux-store";

interface PropsType{
    url: number,
    deleteNew?: (folderID:string, productID:string) => void,
    folderID? :string,
    productID? :string,
}

export const ShowImgInRow:React.FC<PropsType> = ({url,deleteNew,folderID,productID,...props}) =>{
    const [vis,setVis] = useState<boolean>(false);
    const dispatch = useDispatch();
    const foldersName = useSelector( (state:AppStateType) => state.folderPage.ArrayFolders);
    if(foldersName.length === 0){
        let user = JSON.parse(String(localStorage.getItem('profile')));
        console.log(user);
        dispatch(setCarFromDB(user._id));
    }
    const numberMainLetter = 'f';
    let baseUrl = `https://cdn3.riastatic.com/photosnew/auto/photo/__${url}${numberMainLetter}.jpg`;
    const openBigImg = () =>{
        setVis(true);
        document.body.scrollTop = document.documentElement.scrollTop = 0;
    }
    const deleteId =()=>{
        if(deleteNew) {
            if(folderID && productID)
            dispatch(deleteCarThunk(folderID,productID));
        }
    }
    return (
        <div className='Change_width'>
            <span onClick={() => openBigImg()}>
                <img src={baseUrl} alt=""/>
            </span>
            <button onClick={ () => deleteId()}>delete</button>
            {vis &&
                <div className='big_img'>
                    <img src={baseUrl} alt=""/>
                    <div className="nsxs">
                        <button onClick={() => setVis(false)}>Close</button>
                    </div>
                </div>
            }
        </div>
    )
}
