import {BaseThunkType, InferActionsTypes} from "../Redux-store";
import {adminApi} from "../../Api/Api";
interface IAddress {
    country: string | null;
    city: string | null;
    addressLine1: string | null;
    addressLine2: string | null;
}

let initialState = {
    email: '' as string,
    avatar: null as null | string,
    avatarId: null as null | string,
    lastName: '' as string,
    firstName: '' as string,
    gender: null as null | string,
    address: null as null | IAddress,
    profession: '' as string,
    searchField: '' as string,
    phone: '' as string,
    roles: '' as string | Array<string>,
}

type initialStateType = typeof initialState;
export const authReducer = (state=initialState, action:ActionsTypes):initialStateType =>{

    switch (action.type) {
        case "SET_AUTH_USER":
            return{
                ...state,
                ...action.payload
            }
        default: {
            return state;
        }
    }
}

export const actions = {
    setAuthUserAC : (payload:initialStateType) => ({type: 'SET_AUTH_USER' , payload} as const),
}
export const authMeTC = (response:any):ThunkType => async (dispatch:any) =>{
    const storedProfile = JSON.parse(localStorage.getItem("profile") || '');
    dispatch(actions.setAuthUserAC(response));
}

export const loginTC = (data:{email: string, password: string}):ThunkType => async (dispatch:any) => {
    try{
        let response = await adminApi.loginMe(data);
        dispatch(authMeTC(response));
        localStorage.setItem("profile", JSON.stringify(response));
    } catch (e) {
        console.log("FUCK");
    }
}

type ActionsTypes = InferActionsTypes<typeof actions>
type ThunkType = BaseThunkType<ActionsTypes>
