import {createRef, useEffect, useState} from "react";
import {Redirect} from "react-router-dom";
import {changeUrl, changeUrl2, deleteIdACThunk, setInitializeProgram} from "../../Redux/reducers/showPageReducer";
import {connect} from "react-redux";
import {ShowImgInRow} from "./ShowImgInRow";

const Show_number = (props) => {
    const [yes,setYes] = useState(false);
    const [CarAvaible,setCarAvaible] = useState(false);
    useEffect(() => {
        if(JSON.parse(localStorage.getItem('testObject'))){
            props.setInitializeProgram();
            setCarAvaible(true);
        }else{
            localStorage.setItem('testObject', JSON.stringify(props.info));
        }
    },[]);

    let val = '';
    let ref = createRef();

    const sendInfo = () =>{
        console.log("tap")
        props.changeUrl(val);
        ref.current.value ='';
        setYes(true);
    }
    const seeValue = (e) =>{
        val = e.target.value;
    }

    if(yes) return <Redirect to={"/show"}/>
    let retrievedObject = JSON.parse(localStorage.getItem('testObject'));
    return (
        <>
        <div className='Car_app'>
            <input type="text" name='x' placeholder='type a number' ref={ref} onChange={seeValue}/>
            <button onClick={()=> sendInfo()}>Check</button>
            <div>Info about Cars</div>
        </div>
            {CarAvaible &&
                <div className='Align_NIW'>
                    {retrievedObject.map(res => <ShowImgInRow url={res.number} deleteIdACThunk={props.deleteIdACThunk}/>)}
                </div>
            }

        </>
    );
}
const MapStateToProps = (state) =>( {
    info: state.showPage.info
})
let ShowContainer = connect(MapStateToProps,{changeUrl,changeUrl2,setInitializeProgram,deleteIdACThunk})(Show_number);
export default ShowContainer;
