import {createRef, useState} from "react";
import {Redirect} from "react-router-dom";
import {changeUrl} from "../../Redux/reducers/showPageReducer";
import {connect} from "react-redux";

export const Show_number = (props) => {
    debugger;
    const [yes,setYes] = useState(false);
    let val = '';
    let ref = createRef();
    const sendInfo = () =>{
        console.log("tap")
        props.addNumber(val);
        ref.current.value ='';
        setYes(true);
    }
    const seeValue = (e) =>{
        val = e.target.value;
    }
    if(yes) return <Redirect to={"/show"}/>
    return (
        <div className='Car_app'>
            <input type="text" name='x' placeholder='type a number' ref={ref} onChange={seeValue}/>
            <button onClick={()=> sendInfo()}>Check</button>
        </div>
    );
}
const MapStateToProps = (state) =>( {
    numberMain: state.showPage.MainNumberURL
})
const mapDispatchToProps =(dispatch) =>{
    return {
        addNumber: (url) =>{
            let action = changeUrl(url);
            dispatch(action);
        }
    }

}
export default connect(MapStateToProps,mapDispatchToProps)(Show_number);
